import { createReducer, on } from '@ngrx/store';
import { matchesReloaded, matchStarted } from './maches.actions';
export class MatchesState {}
const initialState = new MatchesState();
initialState.currentMatch = null;
initialState.lastMatches = [];
export function machesReducer(s, a) {
  return createReducer(initialState, on(matchStarted, (state, {
    match
  }) => ({
    ...state,
    currentMatch: match
  })), on(matchesReloaded, (state, {
    matches
  }) => ({
    ...state,
    lastMatches: matches
  })))(s, a);
}
