import { createReducer, on } from '@ngrx/store';
import { playersLoaded } from './players.action';
export class PlayersState {}
export const initialState = new PlayersState();
initialState.list = [];
export function playersReducer(s, a) {
  return createReducer(initialState, on(playersLoaded, (state, {
    players
  }) => ({
    ...state,
    list: players
  })))(s, a);
}
